import React, { useState } from 'react'
import { useHistory } from 'react-router'

/**
* @author
* @function Quiz
**/

// We are going to have a list of questions
// from this list we're going to randomly select an index
// this index will be stored in memory and deleted from the other array
// we then run an conditional statement to determine what are the answers
// we then concatinate when the answer is correct or do nothing if it is not. 
// we're nor going to call and random search function on the old array for another question. 

const Quiz = (props) => {
    const [ans, setAns] = useState({
        a: '',
        b: '',
        c: '',
        d: ''
    })
    const list_of_questions = ['how are you?', 'are you okay?', 'where is john?', 'hows mom?', 'what do you do?']
    const history = useHistory()
    var completed = []

    const randList = () => {
        let random = Math.round(Math.random() * 4)
        var newList = list_of_questions
        const check_completed = completed.find(element => element == random)

        console.log(check_completed)

        if (completed.length >= newList.length) {
            console.log('redirect to new page')
        } else if (check_completed || check_completed === 0) {
                randList()
            }
            else{
                completed.push(random)
                console.log(completed)
                _switch(random)
            }

    }

    const _switch = (n) =>{
        switch (n) {
            case 0:
                setAns({ a: 'yes', b: 'no', c: 'maybe', d:'idk'})
                console.log('switch 0')
                break;
            case 1:
                setAns({ a: 'Good', b: 'not so good', c: 'bad', d:'terrible'})
                console.log('switch 1')
                break;
            case 2:
                setAns({ a: 'school', b: 'park', c: 'playground', d:'beach'})
                console.log('switch 2')
                break;
            case 3:
                setAns({ a: 'okay', b: 'alive', c: 'struggling', d:'sad'})
                console.log('switch 3')
                break;
            case 4:
                setAns({ a: 'vet', b: 'driver', c: 'cleaner', d:'hacker'})
                console.log('switch 4')
                break;
            default:
                break;

        }
    }







    return (
        <div id='Prizing'>
            <button onClick={randList}>Select item from list</button>
            <h1 style={{color: 'white'}}>{ans.a}</h1>
            <h1 style={{color: 'white'}}>{ans.b}</h1>
            <h1 style={{color: 'white'}}>{ans.c}</h1>
            <h1 style={{color: 'white'}}>{ans.d}</h1>

        </div>
    )

}

export default Quiz

