import React, { useEffect, useRef } from 'react'
import { useHistory } from 'react-router'

/**
* @author
* @function Quiz
**/

// We are going to have a list of questions
// from this list we're going to randomly select an index
// this index will be stored in memory and deleted from the other array
// we then run an conditional statement to determine what are the answers
// we then concatinate when the answer is correct or do nothing if it is not. 
// we're nor going to call and random search function on the old array for another question. 

const Quiz = (props) => {
    var completed = []
    var test = true;
    const question = useRef()
    const a = useRef()
    const b = useRef()
    const c = useRef()
    const d = useRef()
    const d_trigger = (display_toggle) => {
        if(display_toggle == false){
            return <span ref={d}></span>
        }
        else if(display_toggle){
            return <span style={{display: `${display_toggle}`}}className='ans' ></span>
        }


    }
    const list_of_questions = ['Saturday 7G Sweet & Sour Ready-to-Roll:', 'Saturday 7G Sweet & Sour Ready-to-Roll is coming in what THC%?', "When speaking about the 1G Blood Orange roduct, Id start with:", 'hows mom?', 'what do you do?']
    const history = useHistory()





    useEffect(()=>{
        randList()
    },[])

    const randList = () => {
        var newList = list_of_questions
        if (completed.length >= newList.length) {
            console.log('redirect to new page')
        } else{
            let random = Math.round(Math.random() * 4)
            const check_completed = completed.find(element => element == random)
            console.log(check_completed)

            if (check_completed || check_completed === 0) {
                randList()
            }
            else{
                completed.push(random)
                question.current.textContent = list_of_questions[random]
                console.log(completed)
                switch(random){
                    case 0:
                        a.current.textContent = 'Is a Sativa Offering'
                        b.current.textContent = 'Is an Indica Offering'
                        c.current.textContent = 'Is a Hybrid Offering'
                        d.current.textContent = ''
                        d_trigger(false)
                    break;
                    case 1:
                        a.current.textContent = '11%-15%'
                        b.current.textContent = '16%-20%'
                        c.current.textContent = '20%+'
                        d.current.textContent = ''
                        d_trigger(false)
                    break;
                    case 2:
                        a.current.textContent = 'The Flavour'
                        b.current.textContent = 'The Value'
                        c.current.textContent = 'The Brand'
                        d.current.textContent = 'The Convenience of 510 Carts'
                        d_trigger(true)
                    break;
                    case 3:
                        a.current.textContent = 'ewqe'
                        b.current.textContent = 'me'
                        c.current.textContent = 'iuo'
                        d.current.textContent = 'greq'
                        d_trigger(true)
                    break;
                    case 4:
                        a.current.textContent = 'Counsellors '
                        b.current.textContent = 'vet'
                        c.current.textContent = 'hacker'
                        d.current.textContent = 'army'
                        d_trigger(true)
                    break;
                }
            }
        }
    }


    return (
        <div className='Prizing'>
            <button onClick={randList}>Select item from list</button>

          <div id='quiz_holder'>
              <div id='title_holder'>
              <h2 ref={question}></h2>
              </div>


            <div id='answer_list'>
            <span className='ans' ref={a}></span>
            <span className='ans' ref={b}></span>
           <span className='ans' ref={c}></span>
           {d_trigger()}
           {/* <span className='ans' ref={d}></span> */}
            </div>
          </div>

        </div>
    )

}

export default Quiz

