import React, {useEffect, useRef} from 'react'
import Next from '../Components/NextButton/Next'
import ButtonImage from './../Assets/next.png'
import './Pages.scss'

/**
* @author
* @function LandingPage
**/

const LandingPage = (props) => {
const days = []
const year = []
const month = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September','october', 'November', 'December']
const province =''
const year_start = 1900
const year_end = 2021
const month_start = 0
const month_end = 31
const newRef = useRef(null)


    const annual_picker = (start, end, location) => { 
      if(start == end){ 
        return 
      }
      start = start + 1 
      if(location == 'y'){ 
        year.push(start)
      }
      if(location == 'd'){ 
        days.push(start)
      }

      annual_picker(start, end, location)
    }
    annual_picker(year_start, year_end, 'y')
    annual_picker(month_start, month_end, 'd')





// const select = document.getElementById('test_click')
//      newRef.current.dispatchEvent(
//   new MouseEvent('mousedown', {
//     view: window, 
//     bubbles: true, 
//     cancelable: false, 
//     buttons: 0
//   })
// )
   const age_verification = () =>{ 
     const select = document.getElementById('test_click')
    open(select)
   }
const open =(elem) => { 
  if(document.createEvent){
    var e = document.createEvent("MouseEvents")
    e.initMouseEvent("mousedown", true, true, window, 0,0,0,0,0, false, false, false, false, 0, null)
    elem[0].dispatchEvent(e)
  }else if(Element.fireEvent){ 
    elem[0].fireEvent("onmousedown")
  }

}



  return (
    <div className='home_bg'>
      <div id='next_btn_holder'>
        <Next image={ButtonImage} class='orange-btn' link='/educational-video' />
      </div>
      <div id='landing_wrapping'>
       <h1 style={{color: 'white'}}>PLEASE VERIFY YOUR AGE </h1>
       <div id='dob_capture'> 
       <div className='select_container marg_10'> 
        <select ref={newRef}  id='test_click'> 
          {
            days.map((value, index) =>{
              return(
                <option className='opitons' key={index}>
                    {value}
                </option>
                )
            })
          }
        </select>
        <div className='bb-container '>
        <div className='blackBox' onClick={age_verification}></div>
        </div>
        </div>

        <div className='select_container'> 
        <select> 
          {
            month.map((value, index) =>{
              return(
                <option className='opitons' key={index}>
                    {value}
                </option>
                )
            })
          }
        </select>
        <div className='bb-container'>
        <div className='blackBox'></div>
        </div>
        </div>

        <div className='select_container '> 
        <select> 
          {
            year.map((value, index) =>{
              return(
                <option className='opitons' key={index}>
                    {value}
                </option>
                )
            })
          }
        </select>
        <div className='bb-container'>
        <div className='blackBox'></div>
        </div>

        </div>

       </div>
      </div>


    </div>

  )

}

export default LandingPage
