          </div>
          <input className='inputs formInput' type='text' id='store' name='store' placeholder='STORE'></input>
          </div>
        </div>

        <input type='submit' id='submit_btn' value='SUBMIT'></input>
      </form>

    </div>
  )

}

export default Datacapture
